
var barra = $('.barra');
var globo = $('.globo');
var retina = $('.retina');
var iris = $('.iris');
var win = $(window);



var position = $(window).scrollTop();

// should start at 0

$(window).scroll(function() {
    var scroll = $(window).scrollTop();
    if(scroll > position) {
      barra.css('transform', 'rotate(' + position + 'deg)');
      globo.css('transform', 'rotate(' + position + 'deg)');
      globo.css({ width:'+=1' , left:'+=1' , top:'+=1'});
      retina.css({ width:'+=1' , right:'+=1' , top:'+=1'});
      barra.css({ width:'+=1', right:'+=1' , top:'+=1'});
      globo.css({ width:'+=1' , left:'+=1' , top:'+=1'});
        console.log('scrollDown');
    } else {
      barra.css('transform', 'rotate(' + position + 'deg)');
      globo.css('transform', 'rotate(' + position + 'deg)');
      globo.css({ width:'-=1' , left:'-=1' , top:'-=1'});
      retina.css({ width:'-=1' , right:'-=1' , top:'-=1'});
      barra.css({ width:'-=1', right:'-=1' , top:'-=1'});
      globo.css({ width:'-=1' , left:'-=1' , top:'-=1'});
         console.log('scrollUp');
    }
    position = scroll;
});
