jQuery(document).ready(function($) {
  var menu = $('.menu');
  var eye = $('.menu img');

  menu.mouseleave(function(){
    $('.dot1').animate({ marginTop: '-10px' , opacity: 1 }, 500);
    $('.dot3').animate({ marginTop: '10px' , opacity: 1 }, 500);
  });
  menu.mouseenter(function(){
    $('.dot1').animate({ marginTop: '0' , opacity: 0.5 }, 500);
    $('.dot3').animate({ marginTop: '0' , opacity: 0.5 }, 500);
  });

});
function openMenu() {
  $('#hiddenMenu').fadeIn();
  $('#hiddenMenu').animate({left:0, opacity:"1"}, 200);
  $('.dot1').fadeOut();
  $('.dot2').fadeOut();
  $('.dot3').fadeOut();
}
function closeMenu() {
  $('#hiddenMenu').animate({left:200, opacity:"0"}, 200);
  $('#hiddenMenu').fadeOut();
  $('.dot1').fadeIn();
  $('.dot2').fadeIn();
  $('.dot3').fadeIn();
}
