jQuery(document).ready(function($) {
  var menu = $('.menu');
  var eye = $('.menu img');

  menu.mouseleave(function(){
    $('.dot1').animate({ marginTop: '-15px' , opacity: 1 }, 500);
    $('.dot3').animate({ marginTop: '15px' , opacity: 1 }, 500);
  });
  menu.mouseenter(function(){
    $('.dot1').animate({ marginTop: '0' , opacity: 0.5 }, 500);
    $('.dot3').animate({ marginTop: '0' , opacity: 0.5 }, 500);
  });


});
function openMenu() {
  $('#hiddenMenu').fadeIn();
  $('.dot1').fadeOut();
  $('.dot2').fadeOut();
  $('.dot3').fadeOut();
}
function closeMenu() {
  $('#hiddenMenu').fadeOut();
}
