function next() {
  if($(window).width() <= 400) {
    $('.first').animate({opacity: '0'}, 100);
    $('.first').animate({marginTop: '-58vh'}, 800);
  }
  else{
    $('.first').animate({marginTop: '-79vh'}, 800);
    $('.first').animate({opacity: '0'}, 400);
  }
  $('.second').fadeIn();
  $('.controls img:first-child').fadeOut();
  $('.controls img:nth-child(2)').fadeIn();
  $('.controls img:nth-child(2)').css("display", "block");

}

function back() {
  if($(window).width() <= 400) {
    $('.first').animate({marginTop: '2vh'}, 400);
    $('.first').animate({opacity: '1'}, 200);
  }
  else{
    $('.first').animate({marginTop: '8vh'}, 400);
    $('.first').animate({opacity: '1'}, 200);
  }
  $('.second').fadeOut();
  $('.controls img:first-child').fadeIn();
  $('.controls img:nth-child(2)').fadeOut();
}


$(document).ready(function() {
  if ($(window).width() <= 800 && $(window).width() > 400) {
    $('.date-line').animate({paddingLeft: '67%'}, 1200);
    $('#date h2').animate({paddingLeft: '55%'}, 1150);
  }
  else if($(window).width() <= 400) {
    $('.date-line').animate({paddingLeft: '47%'}, 1200);
    $('#date h2').animate({paddingLeft: '37%'}, 1150);
  }
  else{
    $('.date-line').animate({paddingLeft: '80%'}, 1200);
    $('#date h2').animate({paddingLeft: '68%'}, 1150);
  }

  $('.go-back').on('click', function (e) {
    e.preventDefault();
    $('#outer-wrapper').animate({scrollTop: 0}, 700);
    window.location.href = "index.html";
    $('#logo').fadeOut();
  });

  $('.go-home').on('click', function (e) {
    e.preventDefault();
    $('#outer-wrapper').animate({scrollTop: 0}, 700);
    window.location.href = "index.html";
    $('#logo').fadeOut();
  });




$(window).on("mousewheel", function (event) {
  var scroll = $("#outer-wrapper").scrollTop();



  var scaleBarra = 1.0 + (scroll / 2000);
  var scaleGlobo = 1.0 + (scroll / 3000);
  var scaleRetina = 1.0 + (scroll / 3500);
  var scaleIris = 1.0 + (scroll / 2500);


  if(scroll>=700){
    $('#logo').fadeIn();
    $("#barra").css('transform', 'rotate(' + scroll + 'deg)' + 'scaleX(' + scaleBarra + ')');
    $("#globo").css('transform', 'rotate(' + scroll + 'deg)' + 'scale(' + scaleGlobo + ')');
    $("#retina").css('transform', 'scale(' + scaleRetina + ')');
    $("#iris").css('transform', 'scale(' + scaleIris + ')');


    $("#barra").animate({marginLeft : "30%" , marginTop : "30%"}, 5000);
    $("#globo").animate({marginLeft : "-35%" , marginTop : "20%"}, 5000);
    $("#retina").animate({marginTop : "-15%" , marginLeft : "-15%"}, 5000);
    $("#iris").animate({marginTop : "-7%" , marginLeft : "35%"}, 5000);


    $('.date-line').animate({paddingLeft: '12%'}, 800);
    $('#date h2').animate({paddingLeft: '0%'}, 800);
  }
  else{
    $('#logo').fadeOut();
  }

  });
});
