function next() {
  $('.first').animate({ marginTop: '-84vh'}, 800);
  $('.second').fadeIn();
  $('.controls img:first-child').fadeOut();
  $('.controls img:nth-child(2)').fadeIn();
  $('.controls img:nth-child(2)').css("display" , "block");

}

function back() {
  $('.first').animate({ marginTop: '7vh'}, 400);
  $('.second').fadeOut();
  $('.controls img:first-child').fadeIn();
  $('.controls img:nth-child(2)').fadeOut();
}
